Character.create!([
  {name: "Spider-Man", modified: "2016-09-28T12:15:21-0400", marvel_id: 1009610, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/50/526548a343e4b/portrait_uncanny.jpg", description: "Bitten by a radioactive spider, high school student Peter Parker gained the speed, strength and powers of a spider. Adopting the name Spider-Man, Peter hoped to start a career using his new abilities. Taught that with great power comes great responsibility, Spidey has vowed to use his powers to help people."},
  {name: "Thanos", modified: "2014-02-19T15:41:57-0500", marvel_id: 1009652, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/40/5274137e3e2cd/portrait_uncanny.jpg", description: "The Mad Titan Thanos, a melancholy, brooding individual, consumed with the concept of death, sought out personal power and increased strength, endowing himself with cybernetic implants until he became more powerful than any of his brethren."},
  {name: "Iron Man", modified: "2016-09-28T12:08:19-0400", marvel_id: 1009368, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/c0/527bb7b37ff55/portrait_uncanny.jpg", description: "Wounded, captured and forced to build a weapon by his enemies, billionaire industrialist Tony Stark instead created an advanced suit of armor to save his life and escape captivity. Now with a new outlook on life, Tony uses his money and intelligence to make the world a safer, better place as Iron Man."},
  {name: "Ant-Man (Scott Lang)", modified: "2016-07-26T10:15:03-0400", marvel_id: 1010801, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/e/20/52696868356a0/portrait_uncanny.jpg", description: ""},
  {name: "Hank Pym", modified: "2013-12-17T18:34:04-0500", marvel_id: 1011490, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/2/b0/5205305343bfd/portrait_uncanny.jpg", description: " "},
  {name: "Super-Skrull", modified: "2013-11-01T16:51:40-0400", marvel_id: 1009639, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/b0/5274138fe6deb/portrait_uncanny.jpg", description: "After the Fantastic Four stopped the Skrulls' attempted invasion of Earth, Skrull Emperor Dorrek invested vast resources into creating a superbeing of his own."},
  {name: "Star-Lord (Peter Quill)", modified: "2014-07-14T20:45:53-0400", marvel_id: 1010733, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/a0/537bc55e8b1f5/portrait_uncanny.jpg", description: "Refusing to miss his opportunity, Peter Jason Quill stole a scoutship from Cape Canaveral, returned to Eve, and shot his way to the appointed spot, where his old rival Harrelson, NASA's choice to be Star-Lord, was awaiting selection, where Quill was transported away in his stead, and transformed into Star-Lord."},
  {name: "Drax", modified: "2013-10-17T14:57:24-0400", marvel_id: 1010735, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/e/d0/526032deabbff/portrait_uncanny.jpg", description: ""},
