  {name: "Punisher", modified: "2017-08-24T11:20:12-0400", marvel_id: 1009515, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/90/5261675f6b22f/portrait_uncanny.jpg", description: ""},
  {name: "Daredevil", modified: "2013-07-01T16:44:00-0400", marvel_id: 1009262, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/d/50/50febb79985ee/portrait_uncanny.jpg", description: "Abandoned by his mother, Matt Murdock was raised by his father, boxer \"Battling Jack\" Murdock, in Hell's Kitchen. Realizing that rules were needed to prevent people from behaving badly, young Matt decided to study law; however, when he saved a man from an oncoming truck, it spilled a radioactive cargo that rendered Matt blind while enhancing his remaining senses. Under the harsh tutelage of blind martial arts master Stick, Matt mastered his heightened senses and became a formidable fighter."},
  {name: "Elektra", modified: "2017-08-21T16:51:16-0400", marvel_id: 1009288, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/8/d0/51114fec4a2c8/portrait_uncanny.jpg", description: ""},
