  {name: "Rhino", modified: "1969-12-31T19:00:00-0500", marvel_id: 1009537, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/8/60/4c003c58f1a0c/portrait_uncanny.jpg", description: ""},
  {name: "Scarlet Witch", modified: "2013-10-18T17:31:00-0400", marvel_id: 1009562, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/70/5261a7d7c394b/portrait_uncanny.jpg", description: ""},
  {name: "Falcon", modified: "2013-02-05T13:34:32-0500", marvel_id: 1009297, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/f/b0/5111505fb7009/portrait_uncanny.jpg", description: "With a mental connection to all birds and a suit that gives him wings to fly, The Falcon has been both the partner to Captain America and an Avenger himself. Whether as a super hero or in his secret identity of social worker Sam Wilson, The Falcon dedicates his life to standing up for others."},
  {name: "Karen Page", modified: "2017-08-21T16:53:37-0400", marvel_id: 1011357, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available/portrait_uncanny.jpg", description: ""},
  {name: "Mary Jane Watson", modified: "2013-11-20T17:15:06-0500", marvel_id: 1009708, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/2/03/528d33d000749/portrait_uncanny.jpg", description: "Mary Jane Watson was the guiding light in Peter Parker's life, and their love had overcome countless obstacles."},
  {name: "Jane Foster", modified: "2012-02-02T14:25:53-0500", marvel_id: 1010329, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/b/90/4ce5a22b0d5f1/portrait_uncanny.jpg", description: ""},
  {name: "Typhoid Mary", modified: "2012-01-11T11:47:38-0500", marvel_id: 1010369, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/1/f0/4c00390aa2403/portrait_uncanny.jpg", description: "Typhoid Mary, an enemy and former lover of Daredevil with psionic powers, including telekinesis, is also truly gravely mentally ill and not entirely responsible for her actions."},
  {name: "Mr. Fantastic", modified: "2013-02-06T11:03:34-0500", marvel_id: 1009459, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/d/c0/51127e9337538/portrait_uncanny.jpg", description: ""},
  {name: "Karolina Dean", modified: "1969-12-31T19:00:00-0500", marvel_id: 1010752, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/d/50/4c00377435871/portrait_uncanny.jpg", description: ""},
  {name: "Molly Hayes", modified: "1969-12-31T19:00:00-0500", marvel_id: 1010691, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/60/4c0037783e8b3/portrait_uncanny.jpg", description: ""},
  {name: "Captain America", modified: "2016-09-06T11:37:19-0400", marvel_id: 1009220, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/50/537ba56d31087/portrait_uncanny.jpg", description: "Vowing to serve his country any way he could, young Steve Rogers took the super soldier serum to become America's one-man army. Fighting for the red, white and blue for over 60 years, Captain America is the living, breathing symbol of freedom and liberty."},
  {name: "Vision", modified: "2016-05-02T15:51:46-0400", marvel_id: 1009697, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/d0/5111527040594/portrait_uncanny.jpg", description: "The metal monstrosity called Ultron created the synthetic humanoid known as the Vision from the remains of the original android Human Torch of the 1940s to serve as a vehicle of vengeance against the Avengers."},
  {name: "May Parker", modified: "2014-04-29T14:18:39-0400", marvel_id: 1009490, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/30/535fec80bb4a6/portrait_uncanny.jpg", description: ""},
  {name: "Ben Parker", modified: "1969-12-31T19:00:00-0500", marvel_id: 1009489, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/8/c0/4c003d00c8ed9/portrait_uncanny.jpg", description: ""},
  {name: "Shadow King", modified: "2011-04-13T12:11:28-0400", marvel_id: 1009573, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/30/4ce5a16fd4182/portrait_uncanny.jpg", description: "One of the oldest and more powerful mutants ever, Amahl Farouk has been a threat to mankind at least for decades. He was the first evil mutant met by Charles Xavier, convincing him of the need of the X-Men."},
  {name: "Invisible Woman", modified: "2013-10-24T13:48:00-0400", marvel_id: 1009366, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/a0/52695b9cd40b6/portrait_uncanny.jpg", description: ""},
  {name: "Human Torch", modified: "2013-10-18T17:31:30-0400", marvel_id: 1009356, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/70/5261a7f7b0917/portrait_uncanny.jpg", description: ""},
  {name: "Black Widow", modified: "2016-01-04T18:09:26-0500", marvel_id: 1009189, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/f/30/50fecad1f395b/portrait_uncanny.jpg", description: ""},
  {name: "Ultron", modified: "2013-10-18T17:32:22-0400", marvel_id: 1009685, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/70/5261a838e93c0/portrait_uncanny.jpg", description: "Arguably the greatest and certainly the most horrific creation of scientific genius Dr. Henry Pym, Ultron is a criminally insane rogue sentient robot dedicated to conquest and the extermination of humanity."},
  {name: "Red Skull", modified: "2016-06-02T12:20:31-0400", marvel_id: 1009535, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/2/03/526036550cd37/portrait_uncanny.jpg", description: ""},
  {name: "Mystique", modified: "2014-06-05T17:08:23-0400", marvel_id: 1009465, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/5/03/5390dc2225782/portrait_uncanny.jpg", description: ""},
  {name: "Black Cat", modified: "2013-10-24T13:07:32-0400", marvel_id: 1009185, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/e/03/526952357d91c/portrait_uncanny.jpg", description: ""},
  {name: "Black Panther", modified: "2014-02-27T11:14:59-0500", marvel_id: 1009187, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/60/5261a80a67e7d/portrait_uncanny.jpg", description: ""},
  {name: "Winter Soldier", modified: "2013-10-21T11:31:10-0400", marvel_id: 1010740, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/d/03/5265478293c1e/portrait_uncanny.jpg", description: ""},
  {name: "Quicksilver", modified: "2014-03-05T13:43:15-0500", marvel_id: 1009524, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/f0/53176ffc42f58/portrait_uncanny.jpg", description: ""}
