  {title: "Avengers (1998) #27", modified: "2014-01-23T11:05:06-0500", issue_number: 27, marvel_id: 17509, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/f/60/58595e8265999/portrait_uncanny.jpg", description: "There's a new Avengers lineup, but not everyone is happy about it! What's really going on behind the scenes?"},
  {title: "Avengers (1998) #28", modified: "2014-01-23T11:05:07-0500", issue_number: 28, marvel_id: 17510, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/4/10/58595f13059bf/portrait_uncanny.jpg", description: "The Avengers assist Silverclaw on a mission in her homeland. But upon discovering the machinations of the wizard Kulan Gath, the team has to go back to the drawing board!"},
