  {title: "Avengers Academy (2010) #2", modified: "2014-03-05T12:34:22-0500", issue_number: 2, marvel_id: 29217, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/30/58d41126a54b7/portrait_uncanny.jpg", description: "Hank Pym, Quicksilver, Tigra, Justice and Speedball are here to mentor the next generation of Earth's Mightiest Heroes...but is one of the students hot for teacher? Plus: Get a glimpse inside the mind of Finesse!"},
  {title: "Avengers Academy (2010) #11", modified: "2014-03-05T12:35:01-0500", issue_number: 11, marvel_id: 29215, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/20/58d427001fbe5/portrait_uncanny.jpg", description: "With no intention to go with her husband, Carina enlists the aid of the Avengers to fight on her behalf. When Korvac defeats some of Earth's mightiest heroes, however any hope of survival depends on Avengers Academy!"},
  {title: "Avengers Academy (2010) #12", modified: "2014-03-05T12:35:02-0500", issue_number: 12, marvel_id: 29216, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/70/58d4281e6a2b6/portrait_uncanny.jpg", description: "Accepting Carina's offer to grant the teens the powers of their future incarnations, the students of the Avengers Academy square off against Korvac! Will the power boost of the future be enough to stop him, and what will it mean for the present?"},
  {title: "Avengers Academy (2010) #21", modified: "2014-03-05T12:35:26-0500", issue_number: 21, marvel_id: 36489, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/4/30/58d27e71339e7/portrait_uncanny.jpg", description: "A classic Avenger is murdered and everyone's a suspect!"},
