  {title: "Astonishing X-Men (2004) #51 (Create Your Own Wedding Variant)", modified: "2012-06-11T14:52:44-0400", issue_number: 51, marvel_id: 43221, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/7/10/4fd63db696284/portrait_uncanny.jpg", description: nil},
  {title: "Avengers (1998) #5", modified: "2016-04-11T10:58:28-0400", issue_number: 5, marvel_id: 17534, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/d/03/5851677a17b82/portrait_uncanny.jpg", description: "It's the Avengers versus the Squadron Supreme! Never have the Earth's Mightiest Heroes battled a team so like themselves and yet, so different! Who is behind this ferocious battle between the two superteams?"},
  {title: "Avengers (1998) #6", modified: "2016-12-14T10:45:58-0500", issue_number: 6, marvel_id: 17545, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/d/e0/5851682900f83/portrait_uncanny.jpg", description: nil},
  {title: "Avengers (1998) #57", modified: "-0001-11-30T00:00:00-0500", issue_number: 57, marvel_id: 17542, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/b0/5863dd6a70a67/portrait_uncanny.jpg", description: "Mr. Hyde thinks he's tough, and he is - maybe even tough enough to fight the Avengers! But a greater power threatens the Earth itself, which suddenly has a lot fewer people on it!"},
  {title: "Avengers (1998) #58", modified: "-0001-11-30T00:00:00-0500", issue_number: 58, marvel_id: 17543, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/7/00/5863de5d71f36/portrait_uncanny.jpg", description: "The world's leaders have disappeared, and only the Avengers can guide Earth out of chaos! But the path leads to an extradimensional realm ruled by an unexpected adversary!"},
  {title: "Avengers (1998) #59", modified: "-0001-11-30T00:00:00-0500", issue_number: 59, marvel_id: 17544, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/f/a0/5863df2fc8abc/portrait_uncanny.jpg", description: "Yellowjacket and the Wasp, trapped in extradimensional torment! To rescue them, the Avengers must appeal to a cosmic entity's good side...literally!"},
  {title: "Avengers (1998) #60", modified: "-0001-11-30T00:00:00-0500", issue_number: 60, marvel_id: 17546, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/5/80/5863e00a0e13b/portrait_uncanny.jpg", description: "The Avengers vs. Scorpio and the Brotherhood of the Ankh! Will even turning the villains' own forces against them be enough?"},
  {title: "Avengers (1998) #61", modified: "-0001-11-30T00:00:00-0500", issue_number: 61, marvel_id: 17547, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/2/e0/586bcdf9c3ecd/portrait_uncanny.jpg", description: "The team gains new international status, but will everyone accept it? Avengers come and go as the new lineup is established and new threats await!"},
  {title: "Avengers (1998) #64", modified: "2015-04-28T12:12:22-0400", issue_number: 64, marvel_id: 17550, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/a/30/586bd0972b9ff/portrait_uncanny.jpg", description: "Adversaries for years, the Falcon and Henry Gyrich are on the verge of fighting it out! What role will the sinister Scarecrow play in the unfolding drama?"},
  {title: "Avengers (1998) #68", modified: "2014-02-11T18:23:02-0500", issue_number: 68, marvel_id: 17554, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/7/30/586bd4318f7d8/portrait_uncanny.jpg", description: "The monstrous mastermind behind the Red Zone stands revealed! But who will survive to expose him?"},
  {title: "Avengers (1998) #69", modified: "2011-06-30T17:24:18-0400", issue_number: 69, marvel_id: 17555, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/e0/586bd504bc79f/portrait_uncanny.jpg", description: "The Red Zone spreads, and the body count rises. With some weakened and others imprisoned, can even the Avengers endure?"},
  {title: "Avengers (1963) #88", modified: "2011-10-11T11:42:36-0400", issue_number: 88, marvel_id: 7341, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/c0/4e946415da75d/portrait_uncanny.jpg", description: nil},
  {title: "Avengers (1963) #146", modified: "2011-10-11T15:50:00-0400", issue_number: 146, marvel_id: 7004, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/a/60/4e949cee75c28/portrait_uncanny.jpg", description: nil},
  {title: "Avengers (1963) #312", modified: "2012-03-27T15:21:06-0400", issue_number: 312, marvel_id: 7189, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/5/b0/588b632100dab/portrait_uncanny.jpg", description: nil},
  {title: "Daredevil (1998) #2", modified: "2013-03-07T19:01:25-0500", issue_number: 2, marvel_id: 15620, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/c0/56326dced92e5/portrait_uncanny.jpg", description: "As Matt begins to investigate his recent forced adoption of the so-called Messiah, new evidence comes to light. The child Matt has been made caretaker of is not the Messiah, but rather the Anti-Christ! Plus, Karen Page returns."},
  {title: "Daredevil (1998) #3", modified: "2013-08-22T11:01:37-0400", issue_number: 3, marvel_id: 15631, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/7/03/56326f5207b28/portrait_uncanny.jpg", description: "Foggy Nelson has been set up! After being accused of killing a client, his mother cuts him loose from the firm. Meanwhile, Daredevil is abducted and then confronted by an angelic stranger looking for the missing child."},
  {title: "Daredevil (1998) #4", modified: "2011-06-24T14:13:00-0400", issue_number: 4, marvel_id: 15642, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/30/56326fbd2422f/portrait_uncanny.jpg", description: "Matt Murdock's mother reveals herself to her son! Can this rendezvous help to clear Matt's head or will it just stir things up again? Plus, Bullseye has been hired to retrieve the child!"},
  {title: "Daredevil (1998) #9", modified: "2015-12-07T12:35:29-0500", issue_number: 9, marvel_id: 15694, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/c0/57224121a9252/portrait_uncanny.jpg", description: "Nelson & Murdock Law Firm is still under construction but that doesn't mean there aren't any clients for Foggy and Matt. They've taken on the case of a police informant working from within Kingpin's organization and Kingpin isn't too happy about that!"},
  {title: "Daredevil (1964) #369", modified: "2017-05-31T11:04:07-0400", issue_number: 369, marvel_id: 8372, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/20/59306e4560aad/portrait_uncanny.jpg", description: "After Daredevil is shot by Black Widow, Karen Page struggles to nurse him back to health. Once the Man Without Fear regains his senses, he’s hot on Widow’s trail! But this manhunt isn’t without obstacle; enter Ursa Major, Soviet Russia's strangest science project!"},
  {title: "Daredevil (1964) #371", modified: "2017-05-31T11:12:32-0400", issue_number: 371, marvel_id: 8375, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/b0/59306dec5c195/portrait_uncanny.jpg", description: "While Matt recovers from his wounds, Karen blows off steam, (and spite), with a shopping spree on Matt’s credit card. Daredevil meets up with Black Widow to discuss the kiss they shared. Is DD’s love with Karen in jeopardy? Has he at long last been caught in Widow’s web?"},
  {title: "Daredevil Visionaries: Kevin Smith (Daredevil Vol. I: Guardian Devil) (Trade Paperback)", modified: "2016-05-02T11:57:17-0400", issue_number: 0, marvel_id: 953, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/1/a0/4bc670bb4cbea/portrait_uncanny.jpg", description: "Daredevil's notion of faith is tested when a mysterious baby winds up on his doorstep. Following the death of his longtime lover, Daredevil must find the strength to continue."},
