  {title: "Avengers (1963) #386", modified: "-0001-11-30T00:00:00-0500", issue_number: 386, marvel_id: 7270, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/10/52615ce9ad3c1/portrait_uncanny.jpg", description: nil},
  {title: "Avengers (1963) #387", modified: "-0001-11-30T00:00:00-0500", issue_number: 387, marvel_id: 7271, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/70/5214f5be90e8b/portrait_uncanny.jpg", description: nil},
  {title: "Avengers Vol. 1: World Trust (Trade Paperback)", modified: "2017-01-03T11:20:09-0500", issue_number: 0, marvel_id: 1098, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/b0/5863dd6a70a67/portrait_uncanny.jpg", description: "The capital cities of every nation on Earth have mysteriously vanished, throwing the planet into anarchy. In this time of extraordinary crisis, the nations of the world turn to the only organization capable of leading them through this time of strife and restoring political, economic and social order; the Avengers. They can defeat any super-villain, but can they govern the entire world? Collecting AVENGERS (Vol. 3) 57-62, and Bill Morrison's Avenger's story from MARVEL DOUBLE-SHOT #2."},
  {title: "Avengers VS (2016) #1", modified: "2016-04-28T15:25:10-0400", issue_number: 1, marvel_id: 60341, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/a/10/57505c6f4efc9/portrait_uncanny.jpg", description: "It's the Avengers versus some of their all-time greatest foes! Powerful Asgardian artifacts are on Earth...but can the Avengers recover them before Loki?"},
  {title: "Avengers/Invaders (2008) #9", modified: "2012-04-10T14:14:05-0400", issue_number: 9, marvel_id: 24549, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/1/c0/4f8475869b358/portrait_uncanny.jpg", description: "Picking up from the jaw-dropping events of Avengers/Invaders #8, our heroes find themselves back in time, with the fate of the very world on their shoulders. Disguised as the heroes of the golden past, Iron Man, Spider-Man, Wolverine, Spider-Woman, Ms. Marvel and Luke Cage now face Nazis, the power of the Cosmic Cube and the Red Skull... Avengers Assemble!\r\nRated T  ...$2.99"},
  {title: "Avengers/Invaders (2008) #10", modified: "2012-04-10T14:18:56-0400", issue_number: 10, marvel_id: 23975, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/4/a0/4f84795eb9257/portrait_uncanny.jpg", description: "The true enemy stands revealed and in a twisted past with no order, two teams of heroes make their way under the Atlantic ocean to confront the enemies of freedom who are planning their next move behind a wall built around Europe. But even with the help of Sgt. Fury and his Howling Commandos, plus Black Panther, it may be too late to stop the end of everything.\r<br>Rated T+ ...$2.99\r<br>\r<br>"},
  {title: "Avengers/Invaders (2008) #10 (BREITWEISER VARIANT)", modified: "-0001-11-30T00:00:00-0500", issue_number: 10, marvel_id: 23974, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/5/d0/4bb521618a0f6/portrait_uncanny.jpg", description: "The true enemy stands revealed and in a twisted past with no order, two teams of heroes make their way under the Atlantic ocean to confront the enemies of freedom who are planning their next move behind a wall built around Europe. But even with the help of Sgt. Fury and his Howling Commandos, plus Black Panther, it may be too late to stop the end of everything.\r\nRated T  ...$2.99"},
  {title: "Black Widow & the Marvel Girls (2009) #3", modified: "-0001-11-30T00:00:00-0500", issue_number: 3, marvel_id: 29743, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/e0/4f79d8511e164/portrait_uncanny.jpg", description: "She's S.H.I.E.L.D. agent Natasha Romanoff, and Nick Fury has two missions for his newest recruit. First, find out everything possible about the mysterious Ms. Marvel, and deal with her if deemed necessary. Secondly, infiltrate the Red Skull's organization and discover the location of one of the Skull's \"Sleeper\" robots before it spells doom for all mankind. Natasha Romanoff, the Black Widow, is soon to find that combining these two cases is the one way to stay alive.\r\nRated A ...$2.99"},
  {title: "Cap Transport (2005) #2", modified: "-0001-11-30T00:00:00-0500", issue_number: 2, marvel_id: 16240, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available/portrait_uncanny.jpg", description: nil},
