  {title: "Amazing Spider-Man (1963) #335", modified: "2013-06-13T17:05:30-0400", issue_number: 335, marvel_id: 6744, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/80/57f50b0be287f/portrait_uncanny.jpg", description: "THE RETURN OF THE SINISTER SIX PART 2 Doc Ock has big plans for the Sinister Six. With Electro and Sandman on board already, Spider-Man is going to have his hands full!"},
  {title: "Amazing Spider-Man (1963) #342", modified: "-0001-11-30T00:00:00-0500", issue_number: 342, marvel_id: 6752, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/a0/57ceebb886006/portrait_uncanny.jpg", description: nil},
  {title: "Amazing Spider-Man (1963) #343", modified: "-0001-11-30T00:00:00-0500", issue_number: 343, marvel_id: 6753, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/a0/57ceee5d133f5/portrait_uncanny.jpg", description: nil},
  {title: "Amazing Spider-Man (1963) #370", modified: "-0001-11-30T00:00:00-0500", issue_number: 370, marvel_id: 6783, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/80/57d172e55f017/portrait_uncanny.jpg", description: nil},
  {title: "Amazing Spider-Man (1963) #371", modified: "-0001-11-30T00:00:00-0500", issue_number: 371, marvel_id: 6784, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/4/50/57d833f85e608/portrait_uncanny.jpg", description: nil},
  {title: "Amazing Spider-Man Annual (1964) #25", modified: "2016-09-22T17:36:07-0400", issue_number: 25, marvel_id: 16900, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/f/20/57bef0ba13dd9/portrait_uncanny.jpg", description: "Peter Parker’s origin story is recounted in this exciting annual issue!  In the first segment, Peter uncovers a plot between Empire State University and corrupt mega-corporation Roxxon. What is the valuable resource Roxxon wishes to possess? In a word…vibranium! Guest-starring the Ghost and Ultron-5!"},
  {title: "Avengers (1998) #19", modified: "2016-04-19T18:15:45-0400", issue_number: 19, marvel_id: 17500, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/c0/58540cc26f8fc/portrait_uncanny.jpg", description: "Following the kidnapping of Hank Pym, the Avengers head to Wakanda where they face a most formidable foe: Alkhema, the bride of Ultron!"},
  {title: "Avengers (1998) #20", modified: "2015-05-07T11:45:15-0400", issue_number: 20, marvel_id: 17502, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/00/58540da5bdc24/portrait_uncanny.jpg", description: "Ultron has slaughtered the people of an entire nation.  As the Avengers plan to retake the besieged country, Ultron captures Wasp, Wonder Man, Vision, and Scarlet Witch; the ones he considers to be his family."},
  {title: "Avengers (1998) #21", modified: "2015-05-07T11:45:19-0400", issue_number: 21, marvel_id: 17503, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/e/90/58595924577e1/portrait_uncanny.jpg", description: "The Avengers go to war! The Avengers fight to retake the fallen nation of Slorenia from Ultron, who plans to use Hank Pym's brain patterns to create his \"children.\""},
  {title: "Avengers (1998) #22", modified: "2015-05-07T11:45:22-0400", issue_number: 22, marvel_id: 17504, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/1/70/585959e0b9571/portrait_uncanny.jpg", description: "As Earth's Mightiest Heroes face wave upon wave of Ultron robots, the Vision must face the real Ultron and free his captive family."},
  {title: "Avengers (1998) #23", modified: "2015-04-28T11:55:35-0400", issue_number: 23, marvel_id: 17505, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/1/f0/58595a969a983/portrait_uncanny.jpg", description: "Amidst public outcry for a more racially diverse team of Avengers, the Vision confronts Wonder Man about the unusual connection that they share."},
  {title: "Agents of Atlas (2009) #4", modified: "2015-11-06T14:24:27-0500", issue_number: 4, marvel_id: 24219, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/b0/563cfe5c34af8/portrait_uncanny.jpg", description: "What a difference a half-century makes: As the Agents of 1958 find themselves battling deep behind the Iron Curtain, the Agents of today are locked in mortal conflict with Captain America! And if Bob Grayson loses his mind, that may very well be the end for the Agents of today!\r\nRated T  ...$2.99"},
  {title: "All-Winners Comics (1941) #18", modified: "-0001-11-30T00:00:00-0500", issue_number: 18, marvel_id: 12564, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available/portrait_uncanny.jpg", description: nil},
  {title: "All-Winners Squad: Band of Heroes (2011) #4", modified: "2012-04-23T10:28:09-0400", issue_number: 4, marvel_id: 29736, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/7/90/4e67d3485e69f/portrait_uncanny.jpg", description: "America's heroes at war! Slo-Mo discovers a secret among his band of brothers that threatens to unravel everything. Why does it remain a mystery today?"},
  {title: "Amazing Spider-Man (1999) #531", modified: "2015-09-15T13:55:43-0400", issue_number: 531, marvel_id: 4065, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/00/57fbaf600bd80/portrait_uncanny.jpg", description: "Peter Parker's trip to Washington with his boss Tony Stark has been a real doozy.  As if suffering politicians who want to impose all sort of new rules on guys like him isn't enough, he's also got to deal with the monstrous might of the Titanium Man!  Good thing for Spidey, his new costume's got some very special features...Get ready, True Believer."},
  {title: "Amazing Spider-Man (1999) #534", modified: "2016-06-06T16:47:18-0400", issue_number: 534, marvel_id: 4431, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/8/a0/56fd2cd1d3111/portrait_uncanny.jpg", description: "CIVIL WAR TIE-IN!  Life couldn't be more complicated -- or more dangerous -- for Peter Parker.  Spider-Man has picked a side in the Civil War that's tearing apart the super hero community, and the decision has ripped apart some of his strongest bonds.  When one of the War's leaders comes to recruit Spidey for his troops, will Spidey stay true to that decision?"},
  {title: "Amazing Spider-Man (1999) #537", modified: "2015-09-15T13:55:51-0400", issue_number: 537, marvel_id: 5245, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/c0/56fd2ec251d97/portrait_uncanny.jpg", description: "CIVIL WAR tie-in: THE WAR AT HOME! Spider-Man's about to embark on what very well might be the most important offensive of the Civil War that's rocking the Marvel Universe."},
  {title: "Avengers Vs. Pet Avengers (2010) #4", modified: "2012-03-15T16:07:24-0400", issue_number: 4, marvel_id: 36653, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/40/4f624bdf7d2d0/portrait_uncanny.jpg", description: "The Dragons went barking up the wrong tree when they started picking bones with the PET AVENGERS!!! FANGS fly and FUR furls as Earth's Mightiest Pets battle FIN FANG FOOM's DRAGON ARMY! And, oh what the heck...THE AVENGERS ARE THERE TOO!!!!!"},
  {title: "Avengers/Invaders (2008) #1", modified: "-0001-11-30T00:00:00-0500", issue_number: 1, marvel_id: 21247, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/60/4f71b93bc1f6c/portrait_uncanny.jpg", description: "Legends Live Again. \r\nThe original Invaders (Captain America , Bucky , Human Torch , Toro , and the Sub-Mariner) return in a twelve issue maxi-series by the award winning team behind EARTH X, Justice and Project Superpowers .The greatest super-team of World War II finds themselves transported from the battlefields of the Second World War to a future they never imagined! Now, the Invaders find themselves confronted by two teams of Avengers who want desperately to believe these heroes are who they say they are, while Tony Stark faces his greatest challenge since the Civil War as he must deal with the \"return\" of Steve Rogers.  Confronted by a world they barely recognize, the Invaders will have to show two teams of the Earth's Mightiest Heroes just what kind of power, courage and sheer determination it took to defeat the forces of unrelenting evil in the Twentieth century. In fact...they may just have to do it again in the Twenty-First.\r\nRated T  ...$2.99"},
  {title: "Avengers/Invaders (2008) #2", modified: "-0001-11-30T00:00:00-0500", issue_number: 2, marvel_id: 21394, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/8/70/4c363bbc15a05/portrait_uncanny.jpg", description: "The cross-time team-up of the year continues with the award winning team behind EARTH X, Justice and Project:\r\nSuperpowers! With World War II's greatest heroes mysteriously transported to 2008, it takes the Mighty Avengers to bring them in before the world realizes that the Invaders are back...including the original Captain America. But will Iron Man and S.H.I.E.L.D. have enough firepower to hold them...or do the Invaders have everyone just where they want them?\r\nRated T  ...$2.99"},
  {title: "Avengers/Invaders (2008) #2 (PERKINS VARIANT)", modified: "-0001-11-30T00:00:00-0500", issue_number: 2, marvel_id: 21393, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/30/4f71ba3fac970/portrait_uncanny.jpg", description: "The cross-time team-up of the year continues with the award winning team behind EARTH X, Justice and Project:\r\nSuperpowers! With World War II's greatest heroes mysteriously transported to 2008, it takes the Mighty Avengers to bring them in before the world realizes that the Invaders are back...including the original Captain America. But will Iron Man and S.H.I.E.L.D. have enough firepower to hold them...or do the Invaders have everyone just where they want them?\r\nRated T  ...$2.99"},
  {title: "Avengers/Invaders (2008) #3", modified: "-0001-11-30T00:00:00-0500", issue_number: 3, marvel_id: 21565, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/c/60/4bb796cce2b71/portrait_uncanny.jpg", description: "The King of Atlantis vs the Prince of Atlantis with an entire kingdom as the ultimate prize. Alex Ross, Jim Krueger and Steve Sadowski continue the year's most surprising cross-time epic with a battle that promises to tear two centuries asunder. Plus, more clues to the mystery of just how the Invaders landed in 2008!\r\nRated T  ...$2.99"},
  {title: "Avengers: Standoff (Trade Paperback)", modified: "2017-10-06T12:26:57-0400", issue_number: 0, marvel_id: 64791, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/80/59d7bc032f374/portrait_uncanny.jpg", description: "On his 75th anniversary, Captain America is about to face a challenge unlike any other. Prepare for an assault on...Pleasant Hill! Three shield-wielders past and present — Steve Rogers, Bucky Barnes and Sam Wilson — find themselves in a standoff, with high and very personal stakes. And as this tense conflict quickly escalates out of control, it draws in three teams of Avengers, the Agents of S.H.I.E.L.D. and more! But it all begins in an idyllic community with friendly neighbors...and a terrible, dark secret. What is going on behind Pleasant Hill's closed doors? Collects Avengers Standoff: Welcome to Pleasant Hill #1, Avengers Standoff: Assault on Pleasant Hill Alpha #1, Agents of S.H.I.E.L.D. (2016) #3-4, Uncanny Avengers (2015) #7-8, All-New, All-Different Avengers #7-8, New Avengers (2015) #8-10, Captain America: Sam Wilson #7-8, Illuminati (2015) #6, Howling Commandos of S.H.I.E.L.D. (2015) #6, Avengers Standoff: Assault on Pleasant Hill Omega #1."},
  {title: "Batroc (2010) #1", modified: "2015-12-04T12:41:56-0500", issue_number: 1, marvel_id: 36945, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/a0/5661d486c28ca/portrait_uncanny.jpg", description: "The phone rings. The voice speaks. They want you to fight Captain America. Again. The man who beat you before. The man who you know will beat you again. What can you say? You say \"Yes\". Batroc: Traceur shows why and takes you every step of the road he walks. It's time to meet the man behind the greatest moustache in the Marvel Universe. Yeah, Stark. You heard."},
  {title: "Amazing Spider-Man (1963) #71", modified: "-0001-11-30T00:00:00-0500", issue_number: 71, marvel_id: 6892, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/2/b0/57adf7dec7da4/portrait_uncanny.jpg", description: nil},
  {title: "Avengers (2010) #14", modified: "2017-11-21T17:09:31-0500", issue_number: 14, marvel_id: 29200, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/e/c0/5a26dee2baa54/portrait_uncanny.jpg", description: "FEAR ITSELF TIE-IN As the Avengers are stretched to the breaking point by the events of FEAR ITSELF, the team faces issues from the inside their ranks. With a sudden roster change, will the team be able to recover in time to defeat FEAR ITSELF?\n\n"},
  {title: "Avengers (1963) #17", modified: "2012-09-12T10:18:23-0400", issue_number: 17, marvel_id: 7030, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/90/5852e7182b5e1/portrait_uncanny.jpg", description: "The trail suddenly heats up in the search for the Hulk! But can the Avengers trust the information they've received? And can they defeat the Minotaur that stands in their way?"},
  {title: "Avengers (1963) #18", modified: "2012-09-12T10:18:37-0400", issue_number: 18, marvel_id: 7041, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/9/c0/4e8f290436edc/portrait_uncanny.jpg", description: "Which Avenger can best the Commissar, iron-fisted dictator of Sin-Cong, one-on-one? The answer isn't who you'd expect!"},
  {title: "Avengers (1963) #19", modified: "2012-09-12T10:18:53-0400", issue_number: 19, marvel_id: 7052, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/6/c0/5852fb4e5c815/portrait_uncanny.jpg", description: "Who is the Swordsman, and what business (or history) does he have with the Avengers? More importantly-is he friend...or foe?"},
  {title: "Avengers (1963) #20", modified: "2012-09-12T10:19:18-0400", issue_number: 20, marvel_id: 7064, image_path: "http://i.annihil.us/u/prod/marvel/i/mg/3/b0/585305ccc5585/portrait_uncanny.jpg", description: "The Swordsman's high-tension conflict with the Avengers continues! But even with Iron Man's good word to vouch for him, will the Avengers let him join the team?"}
])
